import React, { useEffect, useState } from 'react';
import './Search.css'
import { useTranslation } from 'react-i18next';
import { useDebounce } from '../../hooks/useDebounce';
import { Navigate, useNavigate } from 'react-router-dom';
import { searchUser } from '../../http/userAPI';
import { SERVER_API } from '../../utils/consts';

const Search = () => {
    const [translation] = useTranslation();
    const [search, setSearch] = useState()
    const [searchResult, setSearchResult] = useState(false)
    const debouncedValue = useDebounce(search, 800)
    const navigate = useNavigate();
    const [userData, setUserData] = useState([])
    useEffect(() =>{
        if(debouncedValue){
            searchUser(debouncedValue)
            .then(result => {
                const data = result.data
                setUserData(data)   
            })
        } else{
            setSearchResult(false)
            setUserData([])
        }
    }, [debouncedValue, searchResult])
    console.log(userData)
    return (
        <div>
            <input 
                id="search" 
                className="search" 
                type="text" 
                placeholder = {translation('search')} 
                onChange={ (e) => setSearch(e.target.value)}
            />
            <div className="result">
            {
                userData.map((data, i) =>(
                    <div key = {i}className="item" onClick={<Navigate to={data?.nickname}/>}>
                        {
                            data?.photo ? 
                                <img className="item__photo" src={SERVER_API + data?.photo} alt=""/>
                            :
                                <div className="item__photo" />
                        }
                        <div className="item__content">
                            <div className="item__content--names">
                                {data.firstname} {data.lastname}
                            </div>
                            <div className="item__content--status">
                                {data.status}
                            </div>
                        </div>
                    </div>
                ))
            }
            </div>
        </div>
        
            
    );
}
export default Search;
