import './Profile.css'
import React, { useContext } from "react";
import { useTranslation } from "react-i18next";
import { userData } from "../../http/userAPI";
import { observer } from "mobx-react";
import { SERVER_API } from "../../utils/consts";
import Menu from "../../components/Menu/Menu"
import { useLoaderData } from 'react-router-dom';
import { UserContext } from '../../providers/UserProvider';
const Profile = observer(() => {
    const [translation] = useTranslation();
    const {userStore} = useContext(UserContext)
    const  data = useLoaderData()
    return (
        <div className="container">
            <div className = "profile">
            <Menu/>
                <div className = "profile__content"> 
                {
                    data?.photo ? 
                        <img className="profile__content--logo" src={SERVER_API + data?.photo} alt=""/>
                    :
                        <div className="profile__content--logo" />
                }
                {
                    data.nickname === userStore.userData.nickname ? 
                        <button className="profile__content--button">
                            {translation("profile.edit")}
                        </button>
                    :
                        <button className="profile__content--button">
                            {translation("profile.send")}
                        </button>
                }
                
                <div className="profile__content--name">
                    <p>{data?.firstname}</p>
                    <p>{data?.lastname}</p>
                </div>
                <div className="profile__content--nickname">
                    {data?.nickname}
                </div>
                <div className="profile__content--gender">
                    {data?.gender}
                </div>
                <div className="profile__content--status">
                    {data?.status}
                </div>
            </div>        
        </div>
    </div>
    )
}
)
export const ProfileLoader = async ({params}) => {
    const res = userData(params.nickname)
    const data = await res
    if (data?.response?.status === 400){
        throw new Response('Page is not found...', {status: 404})
    }
    return data
}
export default Profile;
