import { CHATLIST_URL, CHAT_URL } from "../utils/consts";
import { 
    // $host, 
    $authhost 
} from "./index";

export const chats = async () => {
    const {data} = await $authhost.get(CHATLIST_URL)
    return data
}
export const chat = async (chatId) => {
    const {data} = await $host.get(CHAT_URL + chatId)
    return data
}
