import './editProfile.css'
import Menu from "../../components/Menu/Menu";
import { useContext, useState } from "react";
import { DEFAULT_ROUTE, SERVER_API } from "../../utils/consts";
import { UserContext } from "../../providers/UserProvider";
import { useTranslation } from "react-i18next";
import { changeFirstname, changeGender, changeLastname, changeNickname, changePhoto, changeStatus, userData } from '../../http/userAPI';
import { useLoaderData, useNavigate } from 'react-router-dom';

const EditProfile = () => {
    const [translation] = useTranslation();
    const {userStore} = useContext(UserContext)
    const [firstname, setFirstname] = useState('');
    const navigate = useNavigate();
    const [lastname, setLastname] = useState('');
    const [gender, setGender] = useState('');
    const [status, setStatus] = useState('');
    const [photo, setPhoto] = useState('');
    const [nickname, setNickname] = useState('');
    const  data = useLoaderData()
    const Send = async () => {
        try {
            if(firstname) {
                await changeFirstname(firstname)
            }
            if(lastname) {
                await changeLastname(lastname)
            }
            if(photo) {
                console.log(photo)
                const data = new FormData()
                data.append('photo', photo)
                console.log(photo)
                changePhoto(data)
            }
            if(gender) {
                await changeGender(gender)
            }
            if(status) {
                await changeStatus(status)
            }
            if(nickname) {
                await changeNickname(nickname)
            }
            // userData(userStore?.userData?.nickname).then(
            //     result => {
            //         userStore.setUserData(result)
            //     }
            // )
            navigate(DEFAULT_ROUTE, { replace: true })
        } catch (error) {
            alert(error.response?.data?.message)
        }
    }
    return(
        <div className="container">
            <div className="section">
                <Menu/>
                <div className="content">
                    <img className="edit__logo" src={SERVER_API + data?.photo} alt=""/>
                    <form action="">
                        <input type="file" 
                            onChange={ e => setPhoto(e.target.files[0])}
                        />
                    </form>
                    
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.firstname")}
                        </div>
                        <input className="edit__row--input" type="text" placeholder={data?.firstname} onChange={ e  => setFirstname(e.target.value)}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.lastname")}
                        </div>
                        <input className="edit__row--input" type="text" placeholder={data?.lastname} onChange={ e  => setLastname(e.target.value)}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.nickname")}
                        </div>
                        <input className="edit__row--input" type="text" placeholder={data?.nickname} onChange={ e  => setNickname(e.target.value)}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.status")}
                        </div>
                        <textarea className="edit__row--textarea" type="text" placeholder={data?.status} onChange={ e  => setStatus(e.target.value)}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.genderTitle")}
                        </div>
                        <select className="edit__row--select" defaultValue="" onChange={ e  => setGender(e.target.value)}>
                            <option value="" hidden>
                                {translation("edit.gender")}
                            </option>
                            <option value="man">
                                {translation("edit.man")}
                            </option>
                            <option value="woman">
                                {translation("edit.woman")}
                            </option>
                        </select>
                    </div>
                    <button className="edit__button" onClick={Send}>
                        {translation("edit.button")}
                    </button>
                </div>
            </div>
        </div>
    )
}
export const EditProfileLoader = async () => {
    const {userStore} = useContext(UserContext)
    console.log()
    const res = userData(userStore.userData?.nickname)
    const data = await res
    if (data?.response?.status === 400){
        throw new Response('Page is not found...', {status: 404})
    }
    return data
}
export default EditProfile;
