import React, { useEffect, useState } from 'react';
import './Search.css'
import { useTranslation } from 'react-i18next';
import { useDebounce } from '../../hooks/useDebounce';
import { searchUser } from '../../http/userAPI';

function Search() {
    const [translation] = useTranslation();
    const [search, setSearch] = useState()
    const [searchResult, setSearchResult] = useState(false)
    const [searchData, setSearchData] = useState([])
    const debouncedValue = useDebounce(search, 800)
    useEffect(() =>{
        if(debouncedValue){
            searchUser(debouncedValue).then(result => {
                result.data.forEach((item) => {
                    let id = 0;
                    setSearchData([
                        ...searchData,
                        {
                            id: id++,
                            firstname: item.firstname,
                            lastname: item.lastname,
                            status: item.status,
                            photo: item.photo
                        }
                    ])

                })
            })
        } else{
            setSearchResult(false)
        }
    }, [debouncedValue, searchData])
    console.log(searchData)
    return (
        <div>
            <input id="search" className="search" type="text" placeholder = {translation('search')} onChange={ (e) => setSearch(e.target.value)}/>
            {
                searchData.map(item => {
                    <div key={item.id}className={searchResult ? "result active" : "result"}>
                        <div className="result__photo">
                            {item.photo}
                        </div>
                        <div className="result__fitstname">
                            {item.firstname}
                        </div>
                        <div className="result__lastname">
                            {item.lastname}
                        </div>
                        <div className="result__status">
                            {item.status}
                        </div>
                    </div>
                })
            }
        </div>
            
    );
}
export default Search;
