import React, { useEffect, useState } from 'react';
import './Search.css'
import { useTranslation } from 'react-i18next';
import { useDebounce } from '../../hooks/useDebounce';
import { searchUser } from '../../http/userAPI';

function Search() {
    const [translation] = useTranslation();
    const [search, setSearch] = useState()
    const [searchResult, setSearchResult] = useState(false)
    const debouncedValue = useDebounce(search, 800)
    const [userData, setData] = useState([])
    useEffect(() =>{
        if(debouncedValue){
            searchUser(debouncedValue)
            .then(result => {
                const data = result.data
                setData(data)   
            })
        } else{
            setSearchResult(false)
        }
    }, [debouncedValue, searchResult])
    console.log(userData)
    return (
        <div>
             {
                userData.forEach((data, i) =>
                (
                    <div key = {i} className="result">
                        <div className="result__photo">
                            {data.photo}
                        </div>
                        <div className="result__fitstname">
                            {data.firstname}
                        </div>
                        <div className="result__lastname">
                            {data.lastname}
                        </div>
                        <div className="result__status">
                            {data.status}
                        </div>
                    </div>
                ))
            }
            <input 
                id="search" 
                className="search" 
                type="text" 
                placeholder = {translation('search')} 
                onChange={ (e) => setSearch(e.target.value)}
            />
           
        </div>
        
            
    );
}
export default Search;
