import './ChatList.css'
import React, { useContext, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { observer } from "mobx-react";
import Menu from "../../components/Menu/Menu"
import { chats } from '../../http/communicationApi';
import { useLoaderData } from 'react-router-dom';
import { CommunicationContext } from '../../providers/CommunicationProvider';
const ChatList = observer(() => {
    // const [translation] = useTranslation();
    // const navigate = useNavigate();
    const {communicationStore} = useContext(CommunicationContext)
    useEffect(() => {
        const result = chats()
        communicationStore.setChatList(result.data)
    },[])
    useEffect(() => {
        const interval = setInterval(async ()=>{
            const result = await chats()
            communicationStore.setChatList(result.data)
        }, 10000)
        return () => clearInterval(interval);
    })
    return (
        <div className="container">
            <div className = "section">
                <Menu/>
                <div className = "content"> 
                    {
                        communicationStore.chatList.map(element =>
                            console.log(element.chat.title)
                        )
                    }
                </div>        
        </div>
    </div>
    )
})
export const ChatsLoader = async () => {
    const res = chats()
    const data = await res
    if (data?.response?.status === 400){
        throw new Response('Page is not found...', {status: 404})
    }
    return data
}
export default ChatList;
