import React, { useEffect, useState } from 'react';
import './Search.css'
import { useTranslation } from 'react-i18next';
import { useDebounce } from '../../hooks/useDebounce';
import { searchUser } from '../../http/userAPI';
import { SERVER_API } from '../../utils/consts';

function Search() {
    const [translation] = useTranslation();
    const [search, setSearch] = useState()
    const [searchResult, setSearchResult] = useState(false)
    const debouncedValue = useDebounce(search, 800)
    const [userData, setUserData] = useState([])
    useEffect(() =>{
        if(debouncedValue){
            searchUser(debouncedValue)
            .then(result => {
                const data = result.data
                setUserData(data)   
            })
        } else{
            setSearchResult(false)
            setUserData([])
        }
    }, [debouncedValue, searchResult])
    console.log(userData)
    return (
        <div>
            <input 
                id="search" 
                className="search" 
                type="text" 
                placeholder = {translation('search')} 
                onChange={ (e) => setSearch(e.target.value)}
            />
            <div className="result">
            {
                userData.map((data, i) =>(
                    <div key = {i}className="item">
                        {
                            data?.photo ? 
                                <img className="item__photo" src={SERVER_API + data?.photo} alt=""/>
                            :
                                <div className="item__photo" />
                        }
                        <div className="result__fitstname">
                            {data.firstname}
                        </div>
                        <div className="result__lastname">
                            {data.lastname}
                        </div>
                        <div className="result__status">
                            {data.status}
                        </div>
                    </div>
                ))
            }
            </div>
        </div>
        
            
    );
}
export default Search;
