import React, {  useEffect, useState } from 'react';
import './Search.css'
import { useTranslation } from 'react-i18next';
import { useDebounce } from '../../hooks/useDebounce';
import { useNavigate } from 'react-router-dom';
import { searchUser } from '../../http/userAPI';
import { SERVER_API } from '../../utils/consts';
import { observer } from "mobx-react"

const Search = observer(() => {
    const [translation] = useTranslation();
    const [search, setSearch] = useState('')
    const navigate = useNavigate();
    const [searchResult, setSearchResult] = useState(false)
    const debouncedValue = useDebounce(search, 800)
    const [userData, setUserData] = useState([])
    useEffect(() =>{
        if(debouncedValue){
            searchUser(debouncedValue)
            .then(result => {
                const data = result.data
                setUserData(data)   
                setSearchResult(true)
                if(data.length === 0){
                    setSearchResult(false)
                }
            })
        } else{
            setSearchResult(false)
            setUserData([])
        }
    }, [debouncedValue, searchResult])
    return (
        <div>
            <input 
                id="search" 
                className="search" 
                type="text"
                value={search}
                placeholder = {translation('search')} 
                onChange={ (e) => setSearch(e.target.value)}
            />

            {
                searchResult ? 
                    <div 
                        className = "result"
                        onMouseLeave={() => {
                            setUserData([])
                            setSearch('')
                        }}>
                        {
                            userData.map((data, i) =>(
                                <div 
                                    key = {i} 
                                    className="item" 
                                    onClick={ () =>  {
                                        setUserData([])
                                        setSearch('')
                                        navigate('/' + data?.nickname)
                                    }}
                                    
                                >
                                    {
                                        data?.photo ? 
                                            <img className="item__photo" src={SERVER_API + data?.photo} alt=""/>
                                        :
                                            <div className="item__photo" />
                                    }
                                    <div className="item__content">
                                        <div className="item__content--names">
                                            {data.firstname} {data.lastname}
                                        </div>
                                        <div className="item__content--status">
                                            {data.status}
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                    </div>
                    : 
                    <div className="error">
                        ошибка
                    </div>
                    }
                    
                </div>
        
            
    );
})
export default Search;
