import './Create.css'
import React, { useContext, useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { observer } from "mobx-react";
import Menu from "../../components/Menu/Menu"
import { chats } from '../../http/communicationApi';
import { useLoaderData, useNavigate } from 'react-router-dom';
import { CommunicationContext } from '../../providers/CommunicationProvider';
import { SERVER_API } from '../../utils/consts';
const Create = observer(() => {
    const [translation] = useTranslation();
    const navigate = useNavigate();
    const [search, setSearch] = useState('')
    const {communicationStore} = useContext(CommunicationContext)
    const debouncedValue = useDebounce(search, 1000)
    const [userData, setUserData] = useState([])
    useEffect(() =>{
        const Search = async (e) => {
            if(debouncedValue){
                try {
                    await searchUser(debouncedValue)
                    .then(result => {
                        if(result) {
                            setUserData(result)  
                        }
                    })
                } catch (error) {
                    alert(error.response?.data?.message)
                }
            } else{
                setUserData([])
            }
        }
        Search()
    }, [debouncedValue])
    return (
        <div className="container">
            <div className = "section">
                <Menu/>
                <div className = "content"> 
                    
                    <div className="create">
                        <div className="create__header">
                            <div className="create__header--title">
                                {translation("create.title")}
                            </div>
                            <button className="create__header--button back" onClick={ () => {
                                navigate(-1)}
                            }/>
                        </div>
                        <input 
                            id="search" 
                            className="create__search" 
                            type="text"
                            value={search}
                            placeholder = {translation('search')} 
                            onChange={ (e) => setSearch(e.target.value)}
                        />
                         <div 
                className = "result"
                onMouseLeave={() => {
                    setUserData([])
                    setSearch('')
                }}>
                {
                    userData.map((data, i) =>(
                        <div 
                            key = {i} 
                            className="item" 
                            onClick={ () =>  {
                                setUserData([])
                                setSearch('')
                                navigate('/' + data?.nickname)
                            }}
                            
                        >
                            {
                                data?.photo ? 
                                    <img className="item__photo" src={SERVER_API + data?.photo} alt=""/>
                                :
                                    <div className="item__photo" />
                            }
                            <div className="item__content">
                                <div className="item__content--names">
                                    {data.firstname} {data.lastname}
                                </div>
                                <div className="item__content--status">
                                    {data.status}
                                </div>
                            </div>
                        </div>
                    ))
                }
            </div>
                    </div>
                </div>        
        </div>
    </div>
    )
})
export default Create;
