import React, { useEffect, useState } from 'react';
import './Search.css'
import { useTranslation } from 'react-i18next';
import { useDebounce } from '../../hooks/useDebounce';
import { searchUser } from '../../http/userAPI';

function Search() {
    const [translation] = useTranslation();
    const [search, setSearch] = useState()
    const [searchResult, setSearchResult] = useState(false)
    const debouncedValue = useDebounce(search, 800)
    const [data, setData] = useState([])
        const poisk = () => {
            let id = 0
            if(debouncedValue){
                searchUser(debouncedValue)
                .then(result => {
                    result.data.map((item) => {
                        setData(() => [
                            ...data,
                            {
                                id: id++,
                                item
                            }]
                        )                    
                    })
                })
            } else{
                setSearchResult(false)
            }
        }
    console.log(data)
    return (
        <div>
            <input 
                id="search" 
                className="search" 
                type="text" 
                placeholder = {translation('search')} 
                onChange={ (e) => 
                    {
                        setSearch(e.target.value)
                        poisk()
                    }
                }
            />
            {/* {
                    <div className={searchResult ? "result active" : "result"}>
                        <div className="result__photo">
                            {d.photo}
                        </div>
                        <div className="result__fitstname">
                            {d.firstname}
                        </div>
                        <div className="result__lastname">
                            {d.lastname}
                        </div>
                        <div className="result__status">
                            {d.status}
                        </div>
                    </div>
            } */}
        </div>
            
    );
}
export default Search;
