import React, {useContext, useEffect} from "react";
import Auth from "./view/Auth/Auth";
import Profile from "./view/Profile/Profile";
import Registration from "./view/Registration/Registration";
import './css/reset.css';
import {Routes, Route, Navigate} from 'react-router-dom'
import { observer } from "mobx-react-lite";
import { checkAuth, userData } from "./http/userAPI";
import { LOGIN_ROUTE, PROFILE_ROUTE, REGISTRATION_ROUTE} from "./utils/consts";
import RequireAuth from "./hoc/RequireAuth";
import { UserContext } from "./providers/UserProvider";
import { jwtDecode } from "jwt-decode"
import Header from "./components/Header/Header";
import Menu from "./components/Menu/Menu";

const App = observer( () => {
  const {userStore} = useContext(UserContext)
  useEffect(() => {
    if(localStorage.getItem("Token")){
      checkAuth().then(responce => {
        const decode = jwtDecode(responce.data)
        userData(decode.nickname).then((data) => {
          userStore.setUserData(data)
          userStore.setProfileUrl(decode.nickname)
        }
      )
      })
    }
  }, [userStore])  
  return (
      <div className="App">
        <div className="header">
          <Header/>
        </div>
        <div className="content">
          <Menu/>
        </div>
          <Routes>
              <Route path="/" index element = { 
                <RequireAuth>
                  <Navigate to={userStore.profileUrl}/>
                </RequireAuth>
              }/>
              <Route path={PROFILE_ROUTE} element = {
                <RequireAuth>
                  <Profile/>
                </RequireAuth>
              }/> 
              <Route path={LOGIN_ROUTE} element = { 
                <Auth/>
              }/>
              <Route path={REGISTRATION_ROUTE} element = {
                 <Registration/> 
              }/>
          </Routes>     
      </div>
  );
})

export default App;
