import './Profile.css'
import React, { useContext, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { userData } from "../../http/userAPI";
import { UserContext } from "../../providers/UserProvider";
import { observer } from "mobx-react";
import { SERVER_API } from "../../utils/consts";
import { useParams } from "react-router-dom";
import Menu from '../../components/Menu/Menu';
import Header from '../../components/Header/Header';

const Profile = observer(() => {
    const {userStore} = useContext(UserContext)
    const [translation] = useTranslation();
    const params = useParams();
    useEffect(() => {
        userData(params.nickname).then(data => {
            userStore.setProfileData(data)
            userStore.setProfileUrl(params.nickname)
        })
    }, [userStore, params.nickname])
    return (
        <div className="container">
            <div className = "profile">
                <Header/>
            <div className = "profile__content"> 
                <Menu/>
                <img className="profile__content--logo" src={SERVER_API + "/" + userStore.profileData.photo} alt=""/>
                <button className="profile__content--button">
                    {translation("profile.edit")}
                </button>
                <div className="profile__content--name">
                    <p>{userStore.profileData.firstname}</p>
                    <p>{userStore.profileData.lastname}</p>
                </div>
                <div className="profile__content--nickname">
                    {userStore.profileData.nickname}
                </div>
                <div className="profile__content--gender">
                    {userStore.profileData.gender}
                </div>
                <div className="profile__content--status">
                    {userStore.profileData.status}
                </div>
            </div>        
        </div>
        </div>
    )
})

export default Profile;
