import './Menu.css'
import React, { useContext } from "react";
import { useTranslation } from 'react-i18next';
import { logout } from "../../http/userAPI"
import { useNavigate } from 'react-router-dom';
import { SETTINGS_ROUTE } from '../../utils/consts';
import { UserContext } from '../../providers/UserProvider';

const Menu = () => {
    const [translation] = useTranslation();
    const navigate = useNavigate();
    const {userStore} = useContext(UserContext)
    const logOut = async () => {
        try {
            await logout()
            userStore.setUserData({})
            userStore.setProfileData({})
            userStore.setProfileUrl({})
        } catch (error) {
            alert(error.response?.data?.message)
        }
    }
    return(
        <div className="menu">
            <ul className="navbar">
                { translation('menu', { returnObjects: true }).map((data) =>
                    <li className= "navbar__item" key = {data.id}>
                        <div className={"navbar__item--icon " + data.icon}></div>
                        <button className= "navbar__item--button" onClick={() => {
                            if (data.id === 1) {
                                navigate('/yancools')
                        } else if ( data.id === 2) {
                            navigate(SETTINGS_ROUTE)
                        } else if ( data.id === 3) {
                            logOut()
                        }
                        }}>
                            {data.text}
                        </button>
                    </li>
                )}
            </ul>      
            <div className="menu__copytight">
                    © И. А. Бабайлов, 2024
            </div> 
        </div>
    );
}
export default Menu;
