import { CHANGEFIRSTNAME_URL, CHANGEGENDER_URL, CHANGELASTNAME_URL, CHANGEPHOTO_URL, CHANGESTATUS_URL, ERROR_ROUTE, LOGIN_URL, LOGOUT_URL, REFRESH_URL, REGISTRATION_URL, SEARCHUSER_URL, USERDATA_URL } from "../utils/consts";
import { $host, $authhost } from "./index";

const navigate = useNavigate();

export const registration = async (login, password, firstname, lastname, gender) => {
    try {
        const token = await $host.post(REGISTRATION_URL, {login, password, firstname, lastname , gender})
        localStorage.setItem('Token', token.data)
        return token
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const login = async (login, password) => {
    try {
        const token = await $host.post(LOGIN_URL, {login, password})
        localStorage.setItem('Token', token.data)
        return token
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const logout = async () => {
    try {
        await $authhost.post(LOGOUT_URL)
        localStorage.removeItem('Token')
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const checkAuth = async () =>{
    try {
        const token = await $host.get(REFRESH_URL)
        localStorage.setItem('Token', token.data)
        return token
    } catch (error) {
        localStorage.removeItem('Token')
    }
}
export const userData = async (userNickname) => {
    try {
        const {data} = await $host.get(USERDATA_URL + userNickname)
        return data
    } catch (error) {
        alert(error.response?.data?.message)
        navigate(ERROR_ROUTE, { replace: true })
    }
}
export const changeFirstname = async (newFirstname) => {
    try {
        await $authhost.post(CHANGEFIRSTNAME_URL, {newFirstname})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const changeLastname = async (newLastname) => {
    try {
        await $authhost.post(CHANGELASTNAME_URL, {newLastname})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const changeGender = async (newGender) => {
    try {
        await $authhost.post(CHANGEGENDER_URL, {newGender})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const changeStatus = async (newStatus) => {
    try {
        await $authhost.post(CHANGESTATUS_URL, {newStatus})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const changePhoto = async (photo) => {
    try {
        await $authhost.post(CHANGEPHOTO_URL, {photo})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const searchUser = async (user) => {
    try {
        await $authhost.post(SEARCHUSER_URL, {user})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
