import { CHANGEFIRSTNAME_URL, CHANGEGENDER_URL, CHANGELASTNAME_URL, CHANGEPHOTO_URL, CHANGESTATUS_URL, LOGIN_URL, LOGOUT_URL, REFRESH_URL, REGISTRATION_URL, USERDATA_URL } from "../utils/consts";
import { $host, $authhost } from "./index";
export const registration = async (login, password, firstname, lastname) => {
    try {
        const token = await $host.post(REGISTRATION_URL, {login, password, firstname, lastname})
        localStorage.setItem('Token', token.data)
        return token
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const login = async (login, password) => {
    try {
        const token = await $host.post(LOGIN_URL, {login, password})
        localStorage.setItem('Token', token.data)
        return token
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const logout = async () => {
    try {
        await $host.post(LOGOUT_URL)
        localStorage.removeItem('Token')
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
export const checkAuth = async () =>{
    try {
        const token = await $host.get(REFRESH_URL)
        localStorage.setItem('Token', token.data)
        return token
    } catch (error) {
        localStorage.removeItem('Token')
    }
}
export const userData = async (userNickname) => {
    try {
        const {data} = await $host.get(USERDATA_URL + userNickname)
        return data
    } catch (error) {
        // alert(error.response?.data?.message)
    }
}
export const changeFirstname = async (newFirstname) => {
    try {
        await $authhost.post(CHANGEFIRSTNAME_URL, {newFirstname})
    } catch (error) {
        alert(error.response?.data?.message)
    }
}
