import React, {useContext, useEffect} from "react";
import Auth from "./view/Auth/Auth";
import Profile from "./view/Profile/Profile";
import Registration from "./view/Registration/Registration";
import { Navigate, createBrowserRouter, RouterProvider} from 'react-router-dom'
import { observer } from "mobx-react";
import { checkAuth, userData } from "./http/userAPI";
import { AUTHORIZATION_ROUTE, DEFAULT_ROUTE, PROFILE_ROUTE, REGISTRATION_ROUTE, SETTINGS_ROUTE} from "./utils/consts";
import { UserContext } from "./providers/UserProvider";
import { jwtDecode } from "jwt-decode"
import Header from "../src/components/Header/Header"
import Settings from "./view/Settings/Settings";
import RequireAuth from "./hoc/RequireAuth";
import WithoutAuth from "./hoc/WithoutAuth";
import Error from "./view/Error/Error";

const App = observer( () => {
  const {userStore} = useContext(UserContext)
  useEffect(() => {
    if(localStorage.getItem("Token")){
      checkAuth().then(responce => {
        const decode = jwtDecode(responce.data)
        userData(decode.nickname).then(data => {
          userStore.setUserData(data)
          userStore.setProfileUrl(data.nickname)
        }
      )
      })
    }
  }, [userStore])

  const router = createBrowserRouter([
    {
      path: "/",
      errorElement: <Error/>,
      element:  
      <RequireAuth>
        <Navigate to={userStore.profileUrl}/>
      </RequireAuth>
    },
    {
      path: "/:nickname",
      errorElement: <Error/>,
      element: 
      <RequireAuth>
        <Profile/>
      </RequireAuth>
    },
    {
      path: "/settings",
      errorElement: <Error/>,
      element:  
      <RequireAuth>
        <Settings/>
      </RequireAuth>
    },
    {
      path: "/auth",
      errorElement: <Error/>,
      element:  
      <WithoutAuth>
        <Auth/>
      </WithoutAuth>
    },
    {
      path: "/registration",
      errorElement: <Error/>,
      element:  
      <WithoutAuth>
        <Registration/> 
      </WithoutAuth>
    }
  ]);
  return (
      <div className="App">
        <Header/>
        <div className="container">
          <RouterProvider router={router} />
        </div>     
      </div>
  );
})

export default App;
