import './editProfile.css'
import Menu from "../../components/Menu/Menu";
import { useContext } from "react";
import { SERVER_API } from "../../utils/consts";
import { UserContext } from "../../providers/UserProvider";
import { useTranslation } from "react-i18next";

const EditProfile = () => {
    const [translation] = useTranslation();
    const {userStore} = useContext(UserContext)
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [firstname, setFirstname] = useState('');
    const [lastname, setLastname] = useState('');
    const [gender, setGender] = useState('');
    return(
        <div className="container">
            <div className="section">
                <Menu/>
                <div className="content">
                    <img className="edit__logo" src={SERVER_API + userStore.userData?.photo} alt=""/>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.firstname")}
                        </div>
                        <input className="edit__row--input" type="text" placeholder={userStore.userData?.firstname}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.lastname")}
                        </div>
                        <input className="edit__row--input" type="text" placeholder={userStore.userData?.lastname}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.status")}
                        </div>
                        <input className="edit__row--input" type="text" placeholder={userStore.userData?.status}/>
                    </div>
                    <div className="edit__row">
                        <div className="edit__row--label">
                            {translation("edit.gender")}
                        </div>
                        <select className="edit__row--select" defaultValue="" onChange={ e  => setGender(e.target.value)}>
                            <option value="" hidden>
                                {translation("register.gender")}
                            </option>
                            <option value="man">
                                {translation("register.man")}
                            </option>
                            <option value="woman">
                                {translation("register.woman")}
                            </option>
                        </select>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default EditProfile;
