import { CHATLIST_URL, CHAT_URL, SENDMESSAGE_URL } from "../utils/consts";
import { $authhost } from "./index";

export const chats = async () => {
    const {data} = await $authhost.get(CHATLIST_URL)
    return data
}
export const chat = async (chatId) => {
    try {
        const {data} = await $authhost.get(CHAT_URL + chatId)
        return data
    } catch (error) {
        return error
    }
}
export const sendMessage = async (chatId, content) => {
    try {
        await $authhost.get(SENDMESSAGE_URL, {contentб, chatId})
    } catch (error) {
        return error
    }
}
